import React from 'react';
import './index.css';
import Typed from 'typed.js';

export default function Home() {
    // Create reference to store the DOM element containing the animation
    const el = React.useRef(null);

    React.useEffect(() => {
        const typed = new Typed(el.current, {
            strings: ['<h1>hi, I\'m Sara *･ﾟ☆</h1>'],
            typeSpeed: 70,
            showCursor: false,
        });

        return () => {
            // Destroy Typed instance during cleanup to stop animation
            typed.destroy();
        };
    }, []);

    return (
        <div id="home" className="mb-5">
            <div id="homeHeader"><span ref={el} /></div>
            <p>welcome to my website!<br></br>
                I'm a developer, designer, and artist currently studying at USC.☀️<br></br>
                I'm passionate about building beautiful and accessible tech.</p>
            <a href="#about">read more</a>
        </div>
    );
}
